mapper.delete:
delete from carousel_info 	
WHERE ID IN
 #{item}

update carousel_info set status1 = '-1' 	
WHERE ID IN
 #{item}

mapper.update:
update carousel_info
name=#{name}, title=#{title}, group_id=#{groupId}, src=#{src}, sort=#{sort}, type=#{type}, status1=#{status1}, remark=#{remark}, create_by=#{createBy}, create_time=#{createTime}, update_by=#{updateBy}, update_time=#{updateTime}
where ID=#{id}

mapper.select:
select a.id id, a.name name, a.title title, a.group_id groupId, a.src src, a.sort sort, a.type type, a.status1 status1, a.remark remark, a.create_by createBy, a.create_time createTime, a.update_by updateBy, a.update_time updateTime from carousel_info a
a.status1 != '-1'
AND ( 1=2
OR a.id like #{item}
)
AND group_id = #{groupId}
order by a.sort asc
