mapper.delete:
delete from sys_site 	
WHERE ID IN
 #{item}

update sys_site set status1 = '-1' 	
WHERE ID IN
 #{item}

mapper.update:
update sys_site
site_name=#{siteName}, domain=#{domain}, title=#{title}, keywords=#{keywords}, description=#{description}, logo=#{logo}, phone=#{phone}, email=#{email}, copyright=#{copyright}, beian=#{beian}, type=#{type}, status1=#{status1}, remark=#{remark}, create_by=#{createBy}, create_time=#{createTime}, update_by=#{updateBy}, update_time=#{updateTime}
where ID=#{id}

mapper.select:
select a.id id, a.site_name siteName, a.domain domain, a.title title, a.keywords keywords, a.description description, a.logo logo, a.phone phone, a.email email, a.copyright copyright, a.beian beian, a.type type, a.status1 status1, a.remark remark, a.create_by createBy, a.create_time createTime, a.update_by updateBy, a.update_time updateTime from sys_site a
a.status1 != '-1'
AND ( 1=2
OR a.id like #{item}
)
