mapper.delete:
delete from product_info 	
WHERE ID IN
 #{item}

update product_info set status1 = '-1' 	
WHERE ID IN
 #{item}

mapper.update:
update product_info
code=#{code}, name=#{name}, keywords=#{keywords}, model=#{model}, price=#{price}, parame=#{parame}, description=#{description}, is_hot=#{isHot}, image=#{image}, unit=#{unit}, bar_code=#{barCode}, stock_count=#{stockCount}, type_id=#{typeId}, sort=#{sort}, status1=#{status1}, remark=#{remark}, create_by=#{createBy}, create_time=#{createTime}, update_by=#{updateBy}, update_time=#{updateTime}
where ID=#{id}

mapper.select:
select a.id id, a.code code, a.name name, a.keywords keywords, a.model model, a.price price, a.parame parame, a.description description, a.is_hot isHot, a.image image, a.unit unit, a.bar_code barCode, a.stock_count stockCount, a.type_id typeId, a.sort sort, a.status1 status1, a.remark remark, a.create_by createBy, a.create_time createTime, a.update_by updateBy, a.update_time updateTime from product_info a
a.status1 != '-1'
AND ( 1=2
OR a.id like #{item}
)
